RegisterNetEvent("Chat.StartTemplates")
AddEventHandler("Chat.StartTemplates", function()
    TriggerEvent("chat:addTemplate", "user", "<img src=\"data:image/svg+xml;utf8;base64,\" height=\"16\"/>  <b>{0}</b>: {1}")
    TriggerEvent("chat:addTemplate", "staff", "<img src=\"data:image/svg+xml;utf8;base64,\" height=\"16\" />  <b>{0}</b>: {1}")
    TriggerEvent("chat:addTemplate", "owner", "<img src=\"data:image/svg+xml;utf8;base64,\" height=\"16\" />  <b>{0}</b>: {1}")
    TriggerEvent("chat:addTemplate", "admin", "<img src=\"data:image/svg+xml;utf8;base64,\" height=\"16\" />  <b>{0}</b>: {1}")
    TriggerEvent("chat:addTemplate", "command", "<img src=\"data:image/png;base64,\" height=\"16\" />  <b>{0}</b>: {1}")
end) 

local WeatherList = {
    "RAIN",
    "FOG",
    "SNOWLIGHT",
    "THUNDER",
    "BLIZZARD",
    "SNOW",
    "MISTY",
    "SUNNY",
    "HIGHPRESSURE",
    "CLEARING",
    "SLEET",
    "DRIZZLE",
    "SHOWER",
    "SNOWCLEARING",
    "OVERCASTDARK",
    "THUNDERSTORM",
    "SANDSTORM",
    "HURRICANE",
    "HAIL",
    "WHITEOUT",
    "GROUNDBLIZZARD",
}

RegisterCommand('ooc', function(source, args)
    TriggerServerEvent("Chat:OOC", args[1])
end, false)

RegisterCommand('goto', function(source, args)
    SetEntityCoords(PlayerPedId(), GetWaypointCoords())
end, false)

RegisterCommand('coords', function(source, args)
    local Coords = GetEntityCoords(PlayerPedId(), false)
    print("vector3("..Coords.x..","..Coords.y..","..Coords.z..")")
end, false)

RegisterCommand("weather", function(source, args)
    if args[1] ~= nil then
        for Index = 1, #WeatherList do
            if args[1] == WeatherList[Index].lower() then
                Citizen.InvokeNative(0x59174F1AFE095B5A, GetHashKey(WeatherList[Index]), true, false, true, true, false)
            end
        end
    else
        print("Invalid Command Input!")
    end
end, false)

RegisterNetEvent('Chat:LOOC')
AddEventHandler('Chat:LOOC', function(id, message)
    local SourcePlayer =  GetPlayerFromServerId(id)
    if PlayerId() == SourcePlayer then
        TriggerEvent("chat:addMessage", {color = {30, 144, 255}, multiline = true, args = {"^0[^3"..id.."^0]^8"..GetPlayerName(id), message}})
    elseif #(GetEntityCoords(PlayerPedId(), false) - GetEntityCoords(GetPlayerPed(SourcePlayer), false)) < 12.50 then
        TriggerEvent("chat:addMessage", {color = {30, 144, 255}, multiline = true, args = {"^0[^3"..id.."^0]^8"..GetPlayerName(id), message}})
    end
end)

RegisterNetEvent('Chat:Action')
AddEventHandler("Chat:Action", function(id, message)
    local SourcePlayer =  GetPlayerFromServerId(id)
    if PlayerId() == SourcePlayer then
        TriggerEvent("chat:addMessage", {color = {30, 144, 255}, multiline = true, args = {exports["rp-core"]:GetCharacterName(), message}})
    elseif #(GetEntityCoords(PlayerPedId(), false) - GetEntityCoords(GetPlayerPed(SourcePlayer), false)) < 12.50 then
        TriggerEvent("chat:addMessage", {color = {30, 144, 255}, multiline = true, args = {exports["rp-core"]:GetCharacterName(), message}})
    end
end)
